INSERT INTO `JtInbwwvcomments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(2, 97, 'تفاصيل من الواجب معرفتها عن حق اللجوء في فرنسا &bull; أطلس', '', 'http://alatlaas.com/%d8%ad%d9%82-%d8%a7%d9%84%d9%84%d8%ac%d9%88%d8%a1-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7/', '50.87.248.226', '2015-08-03 12:01:08', '2015-08-03 11:01:08', '[&#8230;] والإقامة في فرنسا, والتي تزداد تشددا يوما بعد يوم, يبقي التجمع العائلي واللجوء الوسيلتين الرئيسيتين للحصول على إقامة طويلة [&#8230;]', 0, '1', '', 'pingback', 0, 0),
(3, 97, 'بان بان', 'banban93@yahoo.com', '', '37.237.144.34', '2015-09-29 21:05:29', '2015-09-29 20:05:29', 'كم شهرا ويصير لم الشمل انا زوجي حاليا في فرنسا', 0, '1', '', '', 0, 0),
(5, 365, 'wael', 'taraeef@hotmail.com', '', '2a01:e34:ed97:a0d0:b8a8:2b83:2800:912e', '2016-02-05 20:54:11', '2016-02-05 20:54:11', 'اخي الله يجزيك الخير\r\nبس اريد توضيح حول لم الشمل بالنسبة للاجئ هل مطلوب منه تقد يم بيانات الدخل او بمعنى ان شروط لم الشمل للاجئ مثل الباقي اي شبه مستحيلة وصعبة\r\nالامر الثاني هل تحسب فترة حصول طالب اللجوء من اول تاريخ حصوله على اذن البقاء الذي مدته 9 اشهر\r\nالامر الثالث بالنسبة لطالب اللجوء الذي اعطي اذن بالبقاء مدة 9 اشهر هل تتم محاكمته و صدور نتيجة الطلب المقدم و اعطائه اقامة مؤقتة خلال مدة 9 اشهر اي يبت بامره في هذه المدة\r\nارجو منكم تفصيل و توضيح هذه الامور اغيثونا ولكم كل الشكر', 0, '1', 'Mozilla/5.0 (Windows NT 6.3; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.103 Safari/537.36', '', 0, 0),
(6, 365, 'hazem', 'hazemalsaied@gmail.com', '', '82.244.100.184', '2016-02-06 12:34:22', '2016-02-06 12:34:22', 'لم الشمل بالعموم ليس مستحيلا .. ولا يتطلب جهدا اكثر من غيره من المعاملات الادارية في فرنسا.\nاجراءات لم الشمل يمكن ان تبدأ بعد حصولك على قرار اللجوء حيث يجب ان تقوم بمراجعة الاوفي في مكان اقامتك لتساعدك بالبدء باجراءات لم الشمل. \n\nنتكلم بحدود معرفتنا. لسنا مصدرا رسميا. وخبرتنا مستقاة من الاجراءات الادارية في مدينة نانسي تحديدا. لذلك قد يكون هنالك بعض التعديلات في غيرها من المدن.', 0, '1', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_10_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.82 Safari/537.36', '', 5, 1),
(7, 97, 'hazem', 'hazemalsaied@gmail.com', '', '82.244.100.184', '2016-02-06 12:34:39', '2016-02-06 12:34:39', 'اجراءات لم الشمل يمكن ان تبدأ بعد حصولك على قرار اللجوء حيث يجب ان تقوم بمراجعة الاوفي في مكان اقامتك لتساعدك بالبدء باجراءات لم الشمل.\n\nنتكلم بحدود معرفتنا. لسنا مصدرا رسميا. وخبرتنا مستقاة من الاجراءات الادارية في مدينة نانسي تحديدا. لذلك قد يكون هنالك بعض التعديلات في غيرها من المدن.', 0, '1', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_10_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/48.0.2564.82 Safari/537.36', '', 3, 1),
(8, 1821, 'كيف تحصل على مساعدات السكن في فرنسا؟ - جمعية التعاون الفرنسي السوري', '', 'http://fse-nancy.fr/%d9%83%d9%8a%d9%81-%d8%aa%d8%ad%d8%b5%d9%84-%d8%b9%d9%84%d9%89-%d9%85%d8%b3%d8%a7%d8%b9%d8%af%d8%a7%d8%aa-%d8%a7%d9%84%d8%b3%d9%83%d9%86-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7', '212.227.174.83', '2016-02-17 18:18:20', '2016-02-17 18:18:20', '[&#8230;] وتصطحب معك: صكّ ملكيّة العقار أو عقد الايجار، وشهادة السكن، وشهادة الولادة، ووثيقة إثباتات الموارد الماليّة [&#8230;]', 0, '1', 'The Incutio XML-RPC PHP Library -- WordPress/4.3.3', 'pingback', 0, 0),
(9, 365, 'asmaa', 'asmaahussain40@yahoo.com', '', '37.237.168.85', '2016-03-20 20:34:39', '2016-03-20 20:34:39', 'سؤالي الاول متى تحسب الاقامه من تاريخ قرار الاوفبرا ام من تاريخ استلامها\r\nسؤالي الثاني شكل الاقامه الموقته تكون على شكل هويه ام بشكل ملصق على الجواز\r\nالسؤال الثالث هل تعطى وثيقة سفر فرنسيه ام البقاء على جوازك الاصلي علما انه مفقود والسلطات الفرنسيه لديها علم بذلك', 0, '1', 'Mozilla/5.0 (Windows NT 6.1; Trident/7.0; rv:11.0) like Gecko', '', 0, 0),
(10, 365, 'hazem', 'hazemalsaied@gmail.com', '', '176.189.64.75', '2016-03-20 20:40:12', '2016-03-20 20:40:12', 'غالبا ما تحسب الإقامة من تاريه قرار الأفبرا .. وتكون الإقامة بطاقة مستقلة .. ولك الحق في طلب وثيقة سفر', 0, '1', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_10_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/49.0.2623.87 Safari/537.36', '', 9, 1),
(11, 95, 'عبد العظيم العلاكي', 'madridi_madridi86@hotmail.com', 'http://Madridi_madridi86@hotmail.com', '105.158.142.65', '2016-03-21 14:24:16', '2016-03-21 14:24:16', 'والدي يحمل الجنسية الفرنسية ما هي الطرق التي يمكنني إتباعها لسماح لي بزيارة عائلتي المقيمة في فرنسا (تولوز) و شكرا', 0, '1', 'Mozilla/5.0 (Linux; Android 4.4.4; GT-I9060I Build/KTU84P) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/49.0.2623.91 Mobile Safari/537.36', '', 0, 0),
(12, 95, 'hazem', 'hazemalsaied@gmail.com', '', '176.189.64.75', '2016-03-21 15:32:24', '2016-03-21 15:32:24', 'ما من خبرة عملية لدينا في هذا المجال .. ولكن يمكن تقديم طلب زيارة مع الاوراق التي تضمن حسن الاستضافة من قبلك كالكفالة وامتلاك منزل كافي للاستضافة ..', 0, '1', 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_10_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/49.0.2623.87 Safari/537.36', '', 0, 1);
