(1896, 'stats_cache', 'a:1:{s:32:"f196a94e19b618b9388627a8b06504f9";a:1:{i:1458670785;a:11:{i:0;a:4:{s:7:"post_id";s:2:"87";s:10:"post_title";s:84:"تفاصيل من الواجب معرفتها عن حق اللجوء في فرنسا";s:14:"post_permalink";s:114:"http://fse-nancy.fr/%d8%ad%d9%82-%d8%a7%d9%84%d9%84%d8%ac%d9%88%d8%a1-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7/";s:5:"views";s:2:"24";}i:1;a:4:{s:7:"post_id";s:2:"97";s:10:"post_title";s:72:"ما الذي يجب معرفته عن لم الشمل في فرنسا؟";s:14:"post_permalink";s:108:"http://fse-nancy.fr/%d9%84%d9%85-%d8%a7%d9%84%d8%b4%d9%85%d9%84-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7/";s:5:"views";s:2:"22";}i:2;a:4:{s:7:"post_id";s:1:"0";s:10:"post_title";s:9:"Home page";s:14:"post_permalink";s:20:"http://fse-nancy.fr/";s:5:"views";s:2:"16";}i:3;a:4:{s:7:"post_id";s:3:"323";s:10:"post_title";s:68:"ما هي بطاقة الاقامة المؤقتة في فرنسا؟";s:14:"post_permalink";s:150:"http://fse-nancy.fr/%d8%a7%d9%84%d8%a7%d9%82%d8%a7%d9%85%d8%a9-%d8%a7%d9%84%d9%85%d8%a4%d9%82%d8%aa%d8%a9-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7/";s:5:"views";s:2:"16";}i:4;a:4:{s:7:"post_id";s:4:"1699";s:10:"post_title";s:92:"تعرّف على أهم التعديلات على قوانين اللجوء في فرنسا";s:14:"post_permalink";s:27:"http://fse-nancy.fr/1699-2/";s:5:"views";s:2:"13";}i:5;a:4:{s:7:"post_id";s:3:"337";s:10:"post_title";s:97:"ما هي أنواع الفيزا الفرنسية التي يمكنك الحصول عليها ؟";s:14:"post_permalink";s:137:"http://fse-nancy.fr/%d8%a3%d9%86%d9%88%d8%a7%d8%b9-%d8%a7%d9%84%d9%81%d9%8a%d8%b2%d8%a7-%d8%a7%d9%84%d9%81%d8%b1%d9%86%d8%b3%d9%8a%d8%a9/";s:5:"views";s:1:"9";}i:6;a:4:{s:7:"post_id";s:2:"95";s:10:"post_title";s:79:"تعرف على أبرز طرق زيارة فرنسا والإقامة فيها";s:14:"post_permalink";s:106:"http://fse-nancy.fr/%d8%b4%d8%b1%d9%88%d8%b7-%d8%b2%d9%8a%d8%a7%d8%b1%d8%a9-%d9%81%d8%b1%d9%86%d8%b3%d8%a7";s:5:"views";s:1:"9";}i:7;a:4:{s:7:"post_id";s:3:"365";s:10:"post_title";s:109:"ما هو الأوفبرا, مكتب حماية اللاجئين وعدمي الجنسية في فرنسا ؟";s:14:"post_permalink";s:149:"http://fse-nancy.fr/%d8%a7%d9%84%d8%a7%d9%88%d9%81%d8%a8%d8%b1%d8%a7-%d8%a7%d9%84%d9%84%d8%ac%d9%88%d8%a1-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7";s:5:"views";s:1:"8";}i:8;a:4:{s:7:"post_id";s:4:"1894";s:10:"post_title";s:94:"معلومات لا بد منها لمعادلة شهادتك الجامعية في فرنسا";s:14:"post_permalink";s:219:"http://fse-nancy.fr/%d9%85%d8%b9%d9%84%d9%88%d9%85%d8%a7%d8%aa-%d9%84%d8%a7-%d8%a8%d8%af-%d9%85%d9%86%d9%87%d8%a7-%d9%84%d9%85%d8%b9%d8%a7%d8%af%d8%a9-%d8%b4%d9%87%d8%a7%d8%af%d8%aa%d9%83-%d8%a7%d9%84%d8%ac%d8%a7%d9%85/";s:5:"views";s:1:"7";}i:9;a:4:{s:7:"post_id";s:3:"314";s:10:"post_title";s:85:"معلومات لا بد منها للحصول على الجنسية الفرنسية";s:14:"post_permalink";s:112:"http://fse-nancy.fr/%d8%a7%d9%84%d8%ac%d9%86%d8%b3%d9%8a%d8%a9-%d8%a7%d9%84%d9%81%d8%b1%d9%86%d8%b3%d9%8a%d8%a9/";s:5:"views";s:1:"7";}i:10;a:4:{s:7:"post_id";s:3:"145";s:10:"post_title";s:91:"احصائيات ستذهلك عن واقع الثقافة والترفيه في فرنسا";s:14:"post_permalink";s:156:"http://fse-nancy.fr/%d8%a7%d9%84%d8%ab%d9%82%d8%a7%d9%81%d8%a9-%d9%88%d8%a7%d9%84%d8%aa%d8%b1%d9%81%d9%8a%d9%87-%d9%81%d9%8a-%d9%81%d8%b1%d9%86%d8%b3%d8%a7/";s:5:"views";s:1:"4";}}}}', 'yes'),
